import axios from "axios"

const baseUrl = "http://localhost:8080"
const token = localStorage.getItem('signature')
condo

export const apiPost = (path, data) => {
    const config = {
        headers:{
            authorization:`Bearer ${token}`
        }

    }
    console.log(config)
    return axios.post(`${baseUrl}${path}`, data, config)
}

export const apiGet = (path, data) => {
    const config = {
        headers:{
            authorization:`Bearer ${token}`
        }
    }
    console.log("222",config)
    return axios.get(`${baseUrl}${path}`, data, config)
}
